  #{sysShopName,jdbcType=VARCHAR}, #{sysCustomerName,jdbcType=VARCHAR}, #{sysBossId,jdbcType=VARCHAR}, 
  #{status,jdbcType=CHAR}, #{customerAccountNo,jdbcType=VARCHAR}, #{createBy,jdbcType=VARCHAR}, 
