mapper.select:
SELECT i.*,i.transaction_amount AS orderAmount, u2.user_type AS userTypeNow, u3.user_type AS nextUserTypeNow, p.pay_date, o.order_id,o.`status`AS orderStatus from income_record i LEFT JOIN sys_user u2 ON i.sys_user_id = u2.id LEFT JOIN sys_user u3 ON i.next_user_id = u3.id LEFT JOIN pay_record p ON i.transaction_id = p.transaction_id LEFT JOIN business_order o ON p.order_id = o.order_id LEFT JOIN income_record_management m ON i.id = m.income_record_id WHERE 1=1
AND i.id = #{requestData.id}
AND i.create_date BETWEEN #{startTime} AND #{endTime}
AND i.income_type = #{requestData.incomeType}
AND i.user_mobile = #{requestData.mobile}
AND i.status = #{requestData.status}
AND i.id not in(select irm.income_record_id from income_record_management irm)
AND m.income_record_id = i.id and m.status='1'
AND i.id not in(select irm.income_record_id from income_record_management irm where irm.status='1' and irm.user_type ='finance-1')
AND i.id not in(select irm.income_record_id from income_record_management irm where irm.status='1' and irm.user_type ='operation-1')
AND i.id not in(select irm.income_record_id from income_record_management irm where irm.status='1') AND i.id in(select irm.income_record_id from income_record_management irm where irm.status='0')
AND i.id in(select irm.income_record_id from income_record_management irm where irm.status='1'and irm.user_type ='finance-1') AND i.id in(select irm.income_record_id from income_record_management irm where irm.status='1'and irm.user_type ='operation-1')
GROUP BY i.id ORDER BY i.create_date DESC

SELECT count(1) count, i.* FROM income_record i,income_record_management m WHERE 1=1
AND i.id = #{requestData.id}
AND i.create_date BETWEEN #{startTime} AND #{endTime}
AND i.income_type = #{requestData.incomeType}
