mapper.select:
SELECT opr.* FROM business_order bo,order_product_relation opr
WHERE bo.order_id=opr.business_order_id and bo.status="3" and
opr.business_product_id=#{productId} and bo.sys_user_id=#{userId} and opr.product_spec=#{productSpec}

mapper.update:
UPDATE order_product_relation SET
id = #{id}

    ,business_order_id=#{businessOrderId}


    ,business_product_id=#{businessProductId}


    ,product_num=#{productNum}


    ,product_spec=#{productSpec}

WHERE
id = #{id}

UPDATE business_order SET
id = #{id}

    ,sys_user_id=#{sysUserId}


    ,order_id=#{businessOrderId}


    ,type=#{type}


    ,status=#{status}


    ,create_date=#{createDate}


    ,update_date=#{updateDate}


    ,user_order_address_id=#{userOrderAddressId}

WHERE
id = #{id}

mapper.select:
SELECT bor.order_id businessOrderId, bpr.product_id businessProductId, opr.product_num businessProductNum, opr.product_spec productSpec, bpr.first_url businessProductFirstUrl, bpr. STATUS productStatus, bpr.product_prefecture, bpr.`name` businessProductName, bpr.price businessProductPrice, bor. STATUS STATUS, bor.update_date updateDate, bor.type type, sa.favorable_price payAmount FROM business_order bor LEFT join order_product_relation opr ON bor.order_id = opr.business_order_id LEFT JOIN business_product bpr ON opr.business_product_id = bpr.product_id LEFT JOIN seckill_activity_field saf on saf.id = bor.seckill_id LEFT JOIN seckill_activity sa on sa.id = saf.activity_id WHERE bor.sys_user_id=#{userId}
and bor.status=#{status}
and bor.status!='del'
and bor.type = #{type}
GROUP BY bor.id ORDER BY bor.update_date DESC

SELECT
o.*,o.order_id AS businessOrderId
FROM business_order o
WHERE
1=1

    AND o.id=#{id}


    AND o.sys_user_id=#{sysUserId}


    AND o.order_id=#{businessOrderId}


    AND o.type=#{type}


    AND o.status=#{status}


    AND o.create_date=#{createDate}


    AND o.update_date=#{updateDate}


    AND o.user_order_address_id=#{userOrderAddressId}

SELECT COUNT(1) from business_order o LEFT JOIN sys_user u on u.id=o.sys_user_id LEFT JOIN order_cop_relation r ON o.order_id=r.order_id LEFT JOIN ( select sys_user_id,order_id,out_trade_no,`status`,openid,transaction_id,amount,pay_type,invoice,max(pay_date) pay_date from pay_record group by order_id ) p ON o.order_id = p.order_id WHERE 1=1
AND (u.mobile=#{param} OR o.order_id = #{param})
AND p.pay_date BETWEEN #{startTime} AND #{endTime}
AND o.update_date BETWEEN #{startTime} AND #{endTime} AND o.status= 4
AND o.status = #{requestData.status}

SELECT ,u.nickname,u.mobile,u.identify_number,p.transaction_id,p.pay_date,p.status AS pay_status,r.waybill_number from business_order o LEFT JOIN sys_user u on u.id=o.sys_user_id LEFT JOIN order_cop_relation r ON o.order_id=r.order_id LEFT JOIN ( select sys_user_id,order_id,out_trade_no,`status`,openid,transaction_id,amount,pay_type,invoice,max(pay_date) pay_date from pay_record group by order_id ) p ON o.order_id = p.order_id WHERE 1=1
AND (u.mobile=#{param} OR o.order_id = #{param})
AND p.pay_date BETWEEN #{startTime} AND #{endTime}
AND o.update_date BETWEEN #{startTime} AND #{endTime} AND o.status= 4
AND o.status = #{requestData.status}
ORDER BY p.pay_date DESC
limit #{pageStartNo},#{pageSize}

SELECT *
FROM
month_transaction_record
where
sys_user_id=#{userId}
AND create_date BETWEEN #{startDate} and #{endDate}

mapper.update:
UPDATE business_order SET
order_id = #{businessOrderId}

    ,sys_user_id=#{sysUserId}


    ,type=#{type}


    ,status=#{status}


    ,create_date=#{createDate}


    ,update_date=#{updateDate}


    ,user_order_address_id=#{userOrderAddressId}

WHERE order_id = #{businessOrderId}

UPDATE order_cop_relation o
SET
o.waybill_number=#{waybillNumber}
WHERE
  o.order_id=#{orderId}

mapper.select:
SELECT id,order_id,waybill_number,transaction_id
FROM order_cop_relation
WHERE 1=1

    AND  id=#{id}


    AND  order_id=#{orderId}


    AND  waybill_number=#{waybillNumber}


    AND  transaction_id=#{transactionId}
